import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import Header from '../components/Header'
import { signup } from '../actions/userActions'
import { useDispatch, useSelector } from 'react-redux'

const MeetingsScreen = (props) => {
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const dispatch = useDispatch()

    const userSignup = useSelector((store) => store.userSignup)
    const { loading, response, error } = userSignup

    // listen on change of the [loading, response, error] values
    useEffect(() => {
        console.log('use effect called: ')
        console.log('loading: ', loading)
        console.log('response: ', response)
        console.log('error: ', error)

        if (response && response.status == 'success') {
            // user successfully got registered
            props.history.push('/signin')
        } else if (error) {
            // there is an error while making the API call
            console.log(error)
            alert('error while making API call')
        }
    }, [loading, response, error])

    const onSignup = () => {
        // console.log('first name: ', firstName)
        // console.log('last name: ', lastName)
        // console.log('email: ', email)
        // console.log('password: ', password)
        dispatch(signup(firstName, lastName, email, password))
    }

    return (
        <div>
            <Header title="Signup Here" />
            <hr />
            <div className="form">

                <div className="mb-3">
                    <label className="form-label">Date and time</label>
                    <input
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}
                        type="date"
                        placeholder="address"
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label">Meeting Description</label>
                    <input
                        onChange={(e) => {
                            setLastName(e.target.value)
                        }}
                        type="email"
                        placeholder="email"
                        className="form-control"></input>
                </div>

                <div className="mb-3">
                    <label className="form-label">status</label>
                    <input
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}

                        placeholder="address"
                        className="form-control"
                    />
                </div>



                <div className="mb-3">
                    <label className="form-label">Password</label>
                    <input
                        onChange={(e) => {
                            setPassword(e.target.value)
                        }}
                        type="password"
                        className="form-control"
                        placeholder="*****"></input>
                </div>

                <div className="mb-3">
                    <label className="form-label">Confirm Password</label>
                    <input
                        onChange={(e) => {
                            setPassword(e.target.value)
                        }}
                        type="password"
                        className="form-control"
                        placeholder="*****"></input>
                </div>

                <div className="mb-3">
                    <label className="form-label">Gender</label><br />

                    <input type="radio" id="male" name="gender" value="male"></input>
                    <label for="male"> Male</label><br />

                    <input type="radio" id="female" name="gender" value="female"></input>
                    <label for="female"> Female</label><br />

                    <input type="radio" id="other" name="gender" value="other"></input>
                    <label for="other"> Other</label>
                </div>

                <div className="mb-3">
                    <label className="form-label">mobile</label>
                    <input
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}

                        placeholder="mobile"
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label">Role</label><br />

                    <input type="radio" name="role"></input>
                    <label for="male"> admin</label><br />

                    <input type="radio" name="role"></input>
                    <label for="female"> employee</label><br />
                </div>

                <div className="mb-3">
                    <label className="form-label">Photo</label>
                    <input
                        onChange={(e) => {
                            setEmail(e.target.value)
                        }}
                        type="file"
                        placeholder="mobile"
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <button onClick={onSignup} className="btn btn-success">
                        Singup
                    </button>

                </div>
            </div>

            {userSignup.loading && <div>waiting for result</div>}
        </div>
    )
}

export default MeetingsScreen
