    //     if (response && response.status == 'success') {
    //         dispatch({
    //             type: PROFILE_UPDATE_RESET,
    //         })
    //         alert('profile updated successfuly')
    //     } else if (error) {
    //         alert('error')
    //     }
    // }, [loading, response, error])

    const onUpdate = () => {
        dispatch(updateProfile(email, phone))
    }

