import { useState, useEffect } from 'react'
import Header from '../components/Header'
import { useDispatch, useSelector } from 'react-redux'
import { updateProfile } from '../actions/employeeActions'
import { PROFILE_UPDATE_RESET } from './../constants/employeeConstants';
import { fetchProfile } from './../actions/employeeActions';

const EditProfileScreen = (props) => {

    const [email, setEmail] = useState('')
    const [phone, setPhone] = useState('')
    const dispatch = useDispatch()

    // const empProfileUpdate = useSelector((store) => store.empProfileUpdate)
    // const { error, response, loading } = empProfileUpdate

    const empProfile = useSelector((store) => store.empProfile)
    const { error, response, loading } = empProfile

    // // call this only once (when the page has loaded successfully)
    useEffect(() => {
        dispatch(fetchProfile())
    }, [])

    useEffect(() => { }, [error, response, loading])

    // useEffect(() => {
    //     console.log('inside useeffect')
    //     console.log(response)
    //     if (response && response.status == 'success') {
    //         dispatch({
    //             type: PROFILE_UPDATE_RESET,
    //         })
    //         alert('profile updated successfuly')
    //     } else if (error) {
    //         alert('error')
    //     }
    // }, [loading, response, error])

    const onUpdate = () => {
        dispatch(updateProfile(email, phone))
    }

    return (
        <div>
            <Header title="Edit Profile" />
            <hr />
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>MeetingId</th>
                        <th>Meeting Info</th>
                        <th>Meeting Date Time</th>
                        <th>Meeting Status</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                {
                    <tbody>
                        {
                            response &&
                            response.data &&
                            response.data.length > 0 &&
                            response.data.map((user) => {
                                return (
                                    <tr>
                                        <td>{user.empId}</td>
                                        <td>{user.empName}</td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                }
            </table>
            <div className="form">
                <div className="mb-3">
                    <label className="form-label">Name</label>
                    <input
                        disabled
                        placeholder="{presponse.data()}"
                        className="form-control"
                    ></input>
                </div>
                <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="email"
                        className="form-control"></input>
                </div>

                <div className="mb-3">
                    <label className="form-label">Phone</label>
                    <input
                        onChange={(e) => setPhone(e.target.value)}
                        placeholder="Phone"
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <label className="form-label">Upload Photo</label>
                    <input
                        type="file"
                        placeholder=""
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <button onClick={onUpdate} className="btn btn-primary">
                        Update
                    </button>

                </div>
            </div>

        </div >
    )
}

export default EditProfileScreen
