import { LEAVE_ADD_SUCCESS } from "../constants/employeeConstants";

import { LEAVE_ADD_REQUEST, LEAVE_ADD_FAIL } from './../constants/employeeConstants';

export const addLeave = (leaveDate, leavePurpose, leaveStatus) => {
    return (dispatch) => {
        dispatch({
            type: LEAVE_ADD_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
            },
        }

        const body = {
            leaveDate,
            leavePurpose,
            leaveStatus
        }

        const url = 'http://localhost:4000/employee/leave'

        axios
            .post(url, body, header)
            .then((response) => {
                dispatch({
                    type: LEAVE_ADD_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: LEAVE_ADD_FAIL,
                    payload: error,
                })
            })
    }
}

import { QUERY_ADD_SUCCESS } from "../constants/employeeConstants";
import { QUERY_ADD_REQUEST, QUERY_ADD_FAIL } from './../constants/employeeConstants';

export const addQuery = (QueryDate, QueryPurpose, QueryStatus) => {
    return (dispatch) => {
        dispatch({
            type: Query_ADD_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
            },
        }

        const body = {
            QueryDate,
            QueryPurpose,
            QueryStatus
        }

        const url = 'http://localhost:4000/employee/Query'

        axios
            .post(url, body, header)
            .then((response) => {
                dispatch({
                    type: QUERY_ADD_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: Query_ADD_FAIL,
                    payload: error,
                })
            })
    }
}
