import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Header from '../components/Header'

const GenerateSalarySlipScreen = (props) => {
  const dispatch = useDispatch()
  const employeeMeetings = useSelector((store) => store.employeeMeetings)
  const { error, response, loading } = employeeMeetings

  const userLogin = useSelector((store) => store.userLogin)

  // // call this only once (when the page has loaded successfully)
  useEffect(() => {
    dispatch(getMeetings())
  }, [])

  useEffect(() => { }, [error, response, loading])

  const onAddNewMeeting = () => {
    props.history.push('/addmeetingscreen')
  }


  return (
    <div>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>Emp Id</th>
            <th>Name</th>
            <th>Email</th>
            <th>No of Present Days </th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {
            response &&
            response.data &&
            response.data.length > 0 &&
            response.data.map((salslip) => {
              return (
                <tr>
                  <td>{salslip.empId}</td>
                  <td>{salslip.noOfPresentDay}</td>
                </tr>
              )
            })
          }
        </tbody>
      </table>
    </div>
  )
}

export default GenerateSalarySlipScreen
