import { createStore, combineReducers, applyMiddleware } from 'redux';
import { userSignupReducer, userLoginReducer, addMeetingReducer, getLeavesReducer, approveLeaveReducer, rejectLeaveReducer } from './reducers/adminReducers';
import logger from 'redux-logger';
import thunk from 'redux-thunk';
import { composeWithDevTools } from 'redux-devtools-extension';
import { employeeAddLeave, employeeAddQuery, employeefetchMeetings } from './reducers/employeeReducers'; // combined reducers

const reducers = combineReducers({
  userSignup: userSignupReducer,
  userLogin: userLoginReducer,
  adminAddMeeting: addMeetingReducer,
  empLeaves: getLeavesReducer,
  empapproveLeave: approveLeaveReducer,
  emprejectLeave: rejectLeaveReducer,
  employeeMeetings: employeefetchMeetings,
  employeeQuerys: employeeAddQuery,
  employeeLeaves: employeeAddLeave
});
const store = createStore(reducers, composeWithDevTools(applyMiddleware(logger, thunk)));
export default store;
