import { createStore, combineReducers, applyMiddleware } from 'redux'
import { userSignupReducer, userLoginReducer, addMeetingReducer } from './reducers/adminReducers'
import logger from 'redux-logger'
import thunk from 'redux-thunk'
import { composeWithDevTools } from 'redux-devtools-extension'
import { employeeAddLeave, employeeAddQuery } from './reducers/employeeReducers';

// combined reducers
const reducers = combineReducers({
  userSignup: userSignupReducer,
  userLogin: userLoginReducer,
  adminAddMeeting: addMeetingReducer,

  employeeaddQuery: employeeAddQuery,
  employeeaddLeave: employeeAddLeave,
})

const store = createStore(
  reducers,
  composeWithDevTools(applyMiddleware(logger, thunk))
)

export default store
