import { QUERY_ADD_SUCCESS, QUERY_ADD_REQUEST, QUERY_ADD_FAIL } from './../constants/employeeConstants';
import { LEAVE_ADD_SUCCESS } from "../constants/employeeConstants";
import axios from 'axios'
import { LEAVE_ADD_REQUEST, LEAVE_ADD_FAIL } from './../constants/employeeConstants';

export const fetchMeetings = () => {

}

export const addLeave = (leaveDate, leavePurpose, leaveStatus) => {
    return (dispatch) => {
        dispatch({
            type: LEAVE_ADD_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
            },
        }

        const body = {
            leaveDate,
            leavePurpose,
            leaveStatus
        }

        const url = 'http://localhost:4000/employee/leave'

        axios
            .post(url, body, header)
            .then((response) => {
                dispatch({
                    type: LEAVE_ADD_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: LEAVE_ADD_FAIL,
                    payload: error,
                })
            })
    }
}


export const addQuery = (queryTitle, queryDesc, queryStatus, empId) => {
    return (dispatch) => {
        dispatch({
            type: QUERY_ADD_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
            },
        }

        const body = {
            queryTitle,
            queryDesc,
            queryStatus,
            empId
        }

        const url = 'http://localhost:4000/employee/Query'

        axios
            .post(url, body, header)
            .then((response) => {
                dispatch({
                    type: QUERY_ADD_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: QUERY_ADD_FAIL,
                    payload: error,
                })
            })
    }
}
