import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Header from '../components/Header';
import { getMeetings } from './../actions/employeeActions';
import { getLeaves } from './../actions/adminActions';

const LeavesScreen = (props) => {
    const dispatch = useDispatch()
    const employeeLeaves = useSelector((store) => store.employeeLeaves)
    const { error, response, loading } = employeeLeaves

    // // // call this only once (when the page has loaded successfully)
    useEffect(() => {
        dispatch(getLeaves())
    }, [])

    useEffect(() => { }, [error, response, loading])

    return (
        <div>

            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>LeaveId</th>
                        <th>Leave Date</th>
                        <th>Leave Purpose</th>
                        <th>Employee Name</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                {
                    <tbody>
                        {
                            response &&
                            response.data &&
                            response.data.length > 0 &&
                            response.data.map((leave) => {
                                return (
                                    <tr>
                                        <td>{leave.leaveId}</td>
                                        <td>{leave.leaveDate}</td>
                                        <td>{leave.leavePurpose}</td>
                                        <td>{leave.leaveStatus}</td>
                                        <td></td>
                                        <td></td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                }
            </table>
        </div>
    )
}

export default LeavesScreen
