import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getLeaves } from './../actions/adminActions';
import { Link } from 'react-router-dom';

const LeavesScreen = (props) => {
    const dispatch = useDispatch()
    const empLeaves = useSelector((store) => store.empLeaves)
    const { error, response, loading } = empLeaves

    const empapproveLeave = useSelector((store) => store.empapproveLeave)
    const { approveerror, approveresponse, approveloading } = empapproveLeave

    const emprejectLeave = useSelector((store) => store.emprejectLeave)
    const { rejecterror, rejectresponse, rejectloading } = emprejectLeave


    // // // call this only once (when the page has loaded successfully)
    useEffect(() => {
        dispatch(getLeaves())
    }, [])

    useEffect(() => { }, [error, response, loading])

    const onApprove = () => {
        dispatch(app)
    }

    const onReject = () => {

    }

    return (
        <div>

            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>LeaveId</th>
                        <th>Leave Date</th>
                        <th>Leave Purpose</th>
                        <th>Employee Name</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                {
                    <tbody>
                        {
                            response &&
                            response.data &&
                            response.data.length > 0 &&
                            response.data.map((leave) => {
                                return (
                                    <tr>
                                        <td>{leave.leaveId}</td>
                                        <td>{leave.leaveDate}</td>
                                        <td>{leave.leavePurpose}</td>
                                        <td>{leave.empname}</td>
                                        <td><Link><a onClick={onApprove}>approve</a></Link></td>
                                        <td><Link><a onClick={onReject}>reject</a></Link></td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                }
            </table>
        </div >
    )
}

export default LeavesScreen
