import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Header from './../components/Header';
import { getPresentDays } from './../actions/employeeActions';

const EditProfileScreen = (props) => {
    const dispatch = useDispatch()
    const empPresent = useSelector((store) => store.empProfile)
    const { error, response, loading } = empPresent

    const userLogin = useSelector((store) => store.userLogin)

    // // call this only once (when the page has loaded successfully)
    useEffect(() => {
        dispatch(getPresentDays())
    }, [])

    useEffect(() => { }, [error, response, loading])

    const onAddNewMeeting = () => {
        props.history.push('/addmeetingscreen')
    }

    return (
        <div>
            <Header title="Edit Profile" />
            <hr />
            {
                userLogin.response && userLogin.response.data.role == 'admin' &&
                <div>
                    <br />
                    <button onClick={onAddNewMeeting} className="btn btn-primary float-start">Add New Meeting</button>
                </div>
            }

            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>MeetingId</th>
                        <th>Meeting Info</th>
                        <th>Meeting Date Time</th>
                        <th>Meeting Status</th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                {
                    <tbody>
                        {
                            response &&
                            response.data &&
                            response.data.length > 0 &&
                            response.data.map((meeting) => {
                                return (
                                    <tr>
                                        <td>{meeting.empId}</td>
                                        <td>{meeting.empName}</td>

                                    </tr>
                                )
                            })
                        }
                    </tbody>
                }
            </table>
        </div>
    )
}

export default EditProfileScreen
