import {
    MEETING_FETCH_REQUEST,
    MEETING_FETCH_SUCCESS,
    MEETING_FETCH_FAIL,
    MEETING_FETCH_RESET,

    QUERY_ADD_SUCCESS,
    QUERY_ADD_REQUEST,
    QUERY_ADD_FAIL,

    LEAVE_ADD_REQUEST,
    LEAVE_ADD_SUCCESS,
    LEAVE_ADD_FAIL
} from '../constants/employeeConstants'

export const employeefetchMeetings = (state = {}, action) => {
    switch (action.type) {
        case MEETING_FETCH_REQUEST:
            return { loading: true }
        case MEETING_FETCH_SUCCESS:
            return { loading: false, response: action.payload }
        case MEETING_FETCH_FAIL:
            return { loading: false, error: action.payload }
        case MEETING_FETCH_RESET:
            return {}
        default:
            return state
    }
}

export const employeeQuery = (state = {}, action) => {
    switch (action.type) {
        case QUERY_ADD_REQUEST:
            return { loading: true }
        case QUERY_ADD_SUCCESS:
            return { loading: false, response: action.payload }
        case QUERY_ADD_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state
    }
}

export const employeeAddQuery = (state = {}, action) => {
    switch (action.type) {
        case QUERY_ADD_REQUEST:
            return { loading: true }
        case QUERY_ADD_SUCCESS:
            return { loading: false, response: action.payload }
        case QUERY_ADD_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state
    }
}

export const employeeAddLeave = (state = {}, action) => {
    switch (action.type) {
        case LEAVE_ADD_REQUEST:
            return { loading: true }
        case LEAVE_ADD_SUCCESS:
            return { loading: false, response: action.payload }
        case LEAVE_ADD_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state
    }
}
