import {
    QUERY_ADD_SUCCESS,
    QUERY_ADD_REQUEST,
    QUERY_ADD_FAIL,

    LEAVE_ADD_SUCCESS,
    LEAVE_ADD_REQUEST,
    LEAVE_ADD_FAIL,

    MEETING_FETCH_REQUEST,
    MEETING_FETCH_SUCCESS,
    MEETING_FETCH_FAIL,

    PROFILE_UPDATE_REQUEST,
    PROFILE_UPDATE_SUCCESS,
    PROFILE_UPDATE_FAIL,
    PROFILE_UPDATE_RESET
} from './../constants/employeeConstants';
import { } from "../constants/employeeConstants";
import axios from 'axios'

export const getMeetings = () => {
    return (dispatch) => {
        dispatch({
            type: MEETING_FETCH_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
                token: sessionStorage['token'],
            },
        }

        const url = 'http://localhost:4000/meeting/getAll'

        axios
            .get(url, header)
            .then((response) => {
                dispatch({
                    type: MEETING_FETCH_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: MEETING_FETCH_FAIL,
                    payload: error,
                })
            })
    }
}

export const updateProfile = (email, phone) => {
    return (dispatch) => {
        dispatch({
            type: QUERIES_STATUS_CHANGE_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
                token: sessionStorage['token'],
            },
        }

        const url = 'http://localhost:4000/query'

        const body = {
            qId, queryStatus,
        }

        axios
            .patch(url, body, header)
            .then((response) => {
                dispatch({
                    type: QUERIES_STATUS_CHANGE_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: QUERIES_STATUS_CHANGE_FAIL,
                    payload: error,
                })
            })
    }
}

export const addLeave = (leaveDate, leavePurpose, leaveStatus) => {
    return (dispatch) => {
        dispatch({
            type: LEAVE_ADD_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
            },
        }

        const body = {
            leaveDate,
            leavePurpose,
            leaveStatus
        }

        const url = 'http://localhost:4000/employee/leave'

        axios
            .post(url, body, header)
            .then((response) => {
                dispatch({
                    type: LEAVE_ADD_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: LEAVE_ADD_FAIL,
                    payload: error,
                })
            })
    }
}

export const addQuery = (queryTitle, queryDesc, queryStatus, empId) => {
    return (dispatch) => {
        dispatch({
            type: QUERY_ADD_REQUEST,
        })

        const header = {
            headers: {
                'Content-Type': 'application/json',
            },
        }

        const body = {
            queryTitle,
            queryDesc,
            queryStatus,
            empId
        }

        const url = 'http://localhost:4000/employee/Query'

        axios
            .post(url, body, header)
            .then((response) => {
                dispatch({
                    type: QUERY_ADD_SUCCESS,
                    payload: response.data,
                })
            })
            .catch((error) => {
                dispatch({
                    type: QUERY_ADD_FAIL,
                    payload: error,
                })
            })
    }
}
