import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import Header from '../components/Header'
import { signup } from '../actions/adminActions'
import { useDispatch, useSelector } from 'react-redux'

const AddQueryScreen = (props) => {

    const [leaveDate, setleaveDate] = useState('')
    const [purpose, setpurpose] = useState('')

    const employeeQuerys = useSelector((store) => store.employeeLeaves)
    const { response, loading, error } = employeeLeaves

    useEffect(() => {
        if (response && response.status == 'success') {
            dispatch({
                type: LEAVE_ADD_RESET,
            })
            alert("Leave Request added Successfully")
        } else if (error) {
            alert(error)
        }
    }, [response, loading, error])

    const dispatch = useDispatch()

    const onAddLeave = () => {
        dispatch(addLeave(leaveDate, purpose, 'pending'))
    }

    return (
        <div>
            <Header title="Add Query" />
            <hr />
            <div className="form">

                <div className="mb-3">
                    <label className="form-label">Title</label>
                    <input
                        className="form-control"></input>
                </div>

                <div className="mb-3">
                    <label className="form-label">Description</label>
                    <input
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <button className="btn btn-primary">
                        Add Query
                    </button>
                </div>
            </div>

        </div>
    )
}

export default AddQueryScreen
